/**
    @overview Demonstrate how to modify the source code before the parser sees it.
    @module plugins/noRest
 */


exports.handlers = {
    ///
    /// Convert ///-style comments into jsdoc comments.
    /// @param e
    /// @param e.filename
    /// @param e.source
    ///
    beforeParse: function(e) {
        e.source = e.source.replace(/\.\.\./g, '_REST_');
    }
};
